import{_ as s,c as l,m as t,a as e,t as o,o as n}from"./chunks/framework.XlwyxPMj.js";const x=JSON.parse('{"title":"开源的心理建设","description":"","frontmatter":{"title":"开源的心理建设"},"headers":[],"relativePath":"pages/collect/202403/mental-health-oss-zh.md","filePath":"pages/collect/202403/mental-health-oss-zh.md","lastUpdated":1711729293000}'),r={name:"pages/collect/202403/mental-health-oss-zh.md"},c={id:"frontmatter-title",tabindex:"-1"},h=t("a",{class:"header-anchor",href:"#frontmatter-title","aria-label":'Permalink to "{{$frontmatter.title}}"'},"​",-1),i=t("div",{class:"warning custom-block"},[t("p",{class:"custom-block-title"},"WARNING"),t("p",null,"作者：antfu"),t("p",null,[e("来源："),t("a",{href:"https://antfu.me/posts/mental-health-oss-zh",target:"_blank",rel:"noreferrer"},"https://antfu.me/posts/mental-health-oss-zh")])],-1),p=t("p",null,"关于开源有趣的一点是 你可能永远不会准备好。你可能会遇到棘手的技术问题，或者需要不断追赶新兴技术的脚步，但除了编码之外，还会有许多其他事情需要你去应对。你要成为 客服 去解答问题；成为 设计师、 作家 去编写一份精美的文档；成为 项目经理 确保项目正常推进；成为 团队领导 来接纳新的贡献者并保持团队的积极性；推广 你的作品；在大会上发表 演讲；等等。",-1),_=t("p",null,"但现实并不会给你充足的时间去做好充足的准备（或者从另一方面来看，如果不迈出第一步，你可能永远不会准备妥当），随着项目的发展，你的职责也随之增加，你会被迫学习和适应。当它自然地成长为一个团队时，你就得学会沟通，学会领导。当有人邀请你做播客或者演讲时，他们不会等你三年时间来练习语言或演讲技巧 —— 你要么错过机会，要么就面对恐惧向前进。",-1),d=t("p",null,"一旦你达成了这些目标，事情就开始变得「理所当然」。",-1),m=t("p",null,"当一个爱好变成一份工作时，你失去了选择什么时候做什么的自由。以前，你会把你的爱好作为工作后的放松，但现在，当你想通过爱好来放松时，它们反倒变成了工作。",-1),f=t("p",null,"我们并不总是有完美的解决方案，但我们必须得继续前进，找到出路。",-1);function u(a,g,k,$,b,z){return n(),l("div",null,[t("h1",c,[e(o(a.$frontmatter.title)+" ",1),h]),i,p,_,d,m,f])}const v=s(r,[["render",u]]);export{x as __pageData,v as default};
